import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity} from 'react-native';
import { useNavigation} from '@react-navigation/native';

export default function Escolha(){
    const navigation = useNavigation(); 
    return(
      < View style={styles.container}>
        <Text style={styles.bancoLogogroup}>YOUNG TEC's</Text>
        <Text style={styles.bancoLogo}>ALERT ZONE's</Text>
          <View>
            <TouchableOpacity  title='Cadastro...' onPress={() => navigation.navigate('Cadastro')} style={styles.botaore}>
             <Text style={styles.botaoTexto}>Registre-se</Text>
             </TouchableOpacity>

              <TouchableOpacity  title='Login...' onPress={() => navigation.navigate('Login')} style={styles.botaolo}>
              <Text style={styles.botaoTexto}>Login</Text>
              </TouchableOpacity>
            </View>
          </View>
  
      );
    }

  const styles = StyleSheet.create({
    container:{
      flex: 1,
      backgroundColor:'#DCDCDC'
    },
    areaformulario:{
      flexDirection: 'column',
      margin: 10,
    },

    botaore:{
      backgroundColor: '#36ad54fa',
      borderRadius: 5,
      color: '#FFF',
    alignItems:'center',
    justifyContent:'center',   
     height: '15%',
     width: '35%',
     position: 'relative',
     left: '5%',
     top: 600,
    },
    botaolo:{
      backgroundColor: '#36ad54fa',
      borderRadius: 5,
      color: '#FFF',
       alignItems:'center',
      justifyContent:'center',   
     height: '15%',
     width: '35%',
      position: 'relative', 
      left: '60%',
      top: 570,
     },
     botaoTexto:{
       fontSize: 20,
       fontWeight: 'bold',
       color: '#FFFFFF'
     },
     bancoLogogroup:{
       textAlign: 'center',
       fontSize: 20,
       fontWeight: 'bold',
       position: 'relative', 
       right: '10%',
       top: '20%',
     },
     bancoLogo:{
      textAlign: 'center',
      fontSize: 20,
      fontWeight: 'bold',
      position: 'relative', 
      left: '5%',
      top: '22%',
    },
  });
