import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Modal, TouchableOpacity, SafeAreaView, TextInput, Alert, ScrollView } from 'react-native';

import axios from 'axios'; 
import {Ionicons} from '@expo/vector-icons';
import * as Animatable from 'react-native-animatable';

export default function App() {

  const mensagemDadosIncorretos = () => 

  Alert.alert(
    "Erro ao Logar",
    "Dados Incorretos!",
    [
      {text: "OK", onPress: () => setAbrirLogin(true)}
    ],
    {cancelable: true}
  );


  function mensagemDelete(id)
  {
    Alert.alert(
      "Excluir Registro",
      "Deseja Excluir este Registro?",
      [
        {text: "Não", 
         onPress: () => console.log("Cancel Pressed"),
         styl:"cancel"
        },
        {
          text: "Sim",
          onPress: () => deleteItem(id)
        }
      ],
      {cancelable: true}
   );
  }


  const mensagemDuplicidade = () => Alert.alert(
     "Erro ao salvar",
     "E-mail já cadastrado",
     [
       {text: "OK", onPress: () => setAbrir(true)}
     ],
     {cancelable: true}
  );

  const api = 'http://192.168.2.136/apireact/';
const [lista,setLista] = useState([]);
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [ruaend, setRuaend] = useState('');
  const [bairro, setBairro] = useState('');
  const [casa, setCasa] = useState('');
  const [id, setId] = useState('');
  const [buscar, setBuscar] = useState('');
  const [abrir, setAbrir] = useState(false);
  const [abrirLogin,setAbrirLogin] = useState(false);
  const [abrirEscolha] = useState(true);

  useEffect(() => {
    listarDados();
  }, [] )

  async function listarDados(){
    const res= await axios.get(api + 'listar.php?busca=' + buscar);
    setLista(res.data.result);
  }

  async function add(){
    const obj={nome,email,senha,ruaend,bairro,casa,id};

    if(id >0)
    {
      const res= await axios.post(api + 'editar.php',obj);
    }
    else{
    const res= await axios.post(api + 'add.php',obj);

    if(res.data.sucess == true)
    {
      limparCampos();
    }
    if(res.data.sucess == 'Email já cadastrado!')
    {
      mensagemDuplicidade();
    }
  } 
    listarDados();
    setAbrir(false);
  }

  async function getItem(id)
  {
    const res= await axios.get(api + 'buscarId.php?id=' + id)
    setId(res.data.id);
    setNome(res.data.nome);
    setEmail(res.data.email);
    setSenha(res.data.senha);
    setRuaend(res.data.ruaend);
    setBairro(res.data.bairro);
    setCasa(res.data.casa);
    setAbrir(true);
  }

  async function deleteItem(id) 
  {
    const res= await axios.get(api + 'excluir.php?id=' + id);
    listarDados();
  } 

  function limparCampos(){
    setNome('');
    setEmail ('');
    setSenha('');
    setRuaend('');
    setBairro('');
    setCasa('');
    setId(0);
  }

  async function login(){
    const obj = {email,senha};
    const res = await axios.post(api + 'login.php' , obj);
     if(res.data.success == 'Dados Incorretos!')
     {
       mensagemDadosIncorretos();
     }
     else
     {
       setAbrirLogin(false);
     }
  }
   function buscarDados()
   {
      listarDados();  
   }


  return (
    <View>
    <View style={estilos.navbar}>
      <Text style={estilos.textonavbar}>Lista de Usuários</Text>

      <TouchableOpacity
        style={estilos.botao}
        onPress={ () => setAbrir(true)}

      >
      <Ionicons name="ios-add" size={30} color="#FFF"></Ionicons>

      </TouchableOpacity>

    </View>

        <View style={estilos.ViewinputBuscar}>
            <TextInput
              style={estilos.inputBuscar}
              placeholder= "Buscar pelo nome"
              value={buscar}
              onChangeText= { (buscar) => setBuscar (buscar)}
              onChange = {buscarDados()}>
            </TextInput>
                <Ionicons style={estilos.iconeBuscar}
                name="ios-search" size={25}
                color="#4b4a49"></Ionicons>  


        </View>
      <ScrollView>
        <View style={estilos.grid}>
          {lista.map(item => (
            <View 
              style={estilos.griditem}
              key={item.id}             
            >
              <Text style={{color:'#585858'}}>
                {item.id} - {item.nome}
              </Text>

              <TouchableOpacity
                style={estilos.gridbotaoEditar}
                onPress={() => getItem(item.id)}

              >

                <Ionicons name="ios-create" size={30} color="#50b9e1"></Ionicons>
              </TouchableOpacity>

              <TouchableOpacity
                style={estilos.gridbotaoExcluir}
                onPress={() => mensagemDelete(item.id)}

              >

                <Ionicons name="ios-trash" size={30} color="#e15f50"></Ionicons>
              </TouchableOpacity>

            </View>

          ))}
        </View>
      </ScrollView>

        <Modal
          animationType='slide'
          visible={abrir}
        > 
        <SafeAreaView style={estilos.modal}>
          <View style={estilos.modalHeader}>
            <TouchableOpacity
              onPress={() => setAbrir(false)} 
            > 
            <Ionicons style={{marginLeft:5, marginRight:5}} name="md-arrow-back" size={35} color="#FFF"> </Ionicons>
            </TouchableOpacity>
            <Text style={estilos.textoModal}> Inserir Morador </Text>
          </View>

            <TextInput
              type="text"
              style={estilos.input}
              placeholder="Insira o seu Email"
              value={email}
              onChangeText={(email) => setEmail(email)}>
            </TextInput>

            <TextInput
              type="text"
              style={estilos.input}
              placeholder="Insira sua senha"
              value={senha}
              onChangeText={(senha) => setSenha(senha)}>
            </TextInput>

            <TextInput
              secureTextEntry={false}
              style={estilos.input}
              placeholder="Insira o nome da sua rua"
              value={ruaend}
              onChangeText={(ruaend) => setRuaend(ruaend)}>
            </TextInput>

            <TextInput
              secureTextEntry={false}
              style={estilos.input}
              placeholder="Insira o nome do seu bairro"
              value={bairro}
              onChangeText={(bairro) => setBairro(bairro)}>
            </TextInput>

            <TextInput
              secureTextEntry={false}
              style={estilos.input}
              placeholder="Insira o numero da sua casa"
              value={casa}
              onChangeText={(casa) => setCasa(casa)}>
            </TextInput>

            <TouchableOpacity style={estilos.botaoModal} onPress={add}>
              <Text style={estilos.textoBotaoModal}> Salvar </Text> 
            </TouchableOpacity>

        </SafeAreaView>
        </Modal>
        <Modal
      animationType="slide"
      transparent={false}
      visible={abrirLogin}
      >
