import React, { useState } from 'react';
import { StyleSheet, Text, View, Modal, TouchableOpacity, SafeAreaView, TextInput} from 'react-native';

import axios from 'axios'; 
import {Ionicons} from '@expo/vector-icons';
import * as Animatable from 'react-native-animatable';

export default function App() {

  const api = 'http://10.68.36.70/apireact/';
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [ruaend, setRuaend] = useState('');
  const [bairro, setBairro] = useState('');
  const [casa, setCasa] = useState('');
  const [id] = useState('');




  async function add(){
    const obj={nome,email,senha,ruaend,bairro,casa};

    if(id >0)
    {
    const res= await axios.post(api + 'add.php',obj);
    if(res.data.sucess == 'Email já cadastrado!')
    {
      mensagemDuplicidade();
    }
  } 
  }



  return (
    <View>
        <Modal>
 <SafeAreaView style={estilos.modal}>         

    <View style={estilos.modalHeader}>
    <TouchableOpacity onPress={() => setAbrir(false)}> 
            <Ionicons style={{marginLeft:5, marginRight:5}} name="md-arrow-back" size={25} color="#363636"> </Ionicons>
    </TouchableOpacity>

       <Text style={estilos.textoModal}>Faça seu cadastro</Text>
    </View>


<TextInput 
type= "text"
 style={estilos.input}
 placeholder="Insira seu Nome"
 value={nome}
 onChangeText={ (nome) => setNome(nome)}>
  </TextInput>

  <TextInput 
  type= "text"
 style={estilos.input}
 placeholder="Insira sua Rua"
 value={ruaend}
 onChangeText={ (ruaend) => setRuaend(ruaend)}>
  </TextInput>

<TextInput 
type= "text"
 style={estilos.input}
 placeholder="Insira seu Bairro"
 value={bairro}
 onChangeText={ (bairro) => setBairro(bairro)}>
  </TextInput>

  <TextInput 
  type= "text"
 style={estilos.input}
 placeholder="Insira sua Casa"
 value={casa}
 onChangeText={ (casa) => setCasa(casa)}>
  </TextInput>

  <TextInput 
 type="email"
 style={estilos.input}
 placeholder="Insira seu Email"
 value={email}
 onChangeText={ (email) => setEmail(email)}>
  </TextInput>

    <TextInput 
    type= "password"
 secureTextEntry={true}
 style={estilos.input}
 placeholder="Insira sua Senha"
 value={senha}
 onChangeText={ (senha) => setSenha(senha)}>
 </TextInput>

<TouchableOpacity  
      style={estilos.botaoModal}
      onPress={add}
      >
    <Text  style={estilos.textoBotaoModal}>Cadastrar</Text>
</TouchableOpacity>

   </SafeAreaView>
  </Modal> 

  </View>
  );
}

