import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Modal, TouchableOpacity, SafeAreaView, TextInput, Alert, ScrollView } from 'react-native';

import axios from 'axios'; 
import {Ionicons} from '@expo/vector-icons';
import * as Animatable from 'react-native-animatable';

export default function App() {

  const mensagemDadosIncorretos = () => 

  Alert.alert(
    "Erro ao Logar",
    "Dados Incorretos!",
    [
      {text: "OK", onPress: () => setAbrirLogin(true)}
    ],
    {cancelable: true}
  );


  function mensagemDelete(id)
  {
    Alert.alert(
      "Excluir Registro",
      "Deseja Excluir este Registro?",
      [
        {text: "Não", 
         onPress: () => console.log("Cancel Pressed"),
         styl:"cancel"
        },
        {
          text: "Sim",
          onPress: () => deleteItem(id)
        }
      ],
      {cancelable: true}
   );
  }


  const mensagemDuplicidade = () => Alert.alert(
     "Erro ao salvar",
     "E-mail já cadastrado",
     [
       {text: "OK", onPress: () => setAbrir(true)}
     ],
     {cancelable: true}
  );

  const api = 'http://192.168.1.73/apireact/';
const [lista,setLista] = useState([]);
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [ruaend, setRuaend] = useState('');
  const [bairro, setBairro] = useState('');
  const [casa, setCasa] = useState('');
  const [id, setId] = useState('');
  const [buscar, setBuscar] = useState('');
  const [abrir, setAbrir] = useState(false);
  const [abrirLogin,setAbrirLogin] = useState(false);
  const [abrirEscolha] = useState(true);

  useEffect(() => {
    listarDados();
  }, [] )

  async function listarDados(){
    const res= await axios.get(api + 'listar.php?busca=' + buscar);
    setLista(res.data.result);
  }

  async function add(){
    const obj={nome,email,senha,ruaend,bairro,casa,id};

    if(id >0)
    {
      const res= await axios.post(api + 'editar.php',obj);
    }
    else{
    const res= await axios.post(api + 'add.php',obj);

    if(res.data.sucess == true)
    {
      limparCampos();
    }
    if(res.data.sucess == 'Email já cadastrado!')
    {
      mensagemDuplicidade();
    }
  } 
    listarDados();
    setAbrir(false);
  }

  async function getItem(id)
  {
    const res= await axios.get(api + 'buscarId.php?id=' + id)
    setId(res.data.id);
    setNome(res.data.nome);
    setEmail(res.data.email);
    setSenha(res.data.senha);
    setRuaend(res.data.ruaend);
    setBairro(res.data.bairro);
    setCasa(res.data.casa);
    setAbrir(true);
  }

  async function deleteItem(id) 
  {
    const res= await axios.get(api + 'excluir.php?id=' + id);
    listarDados();
  } 

  function limparCampos(){
    setNome('');
    setEmail ('');
    setSenha('');
    setRuaend('');
    setBairro('');
    setCasa('');
    setId(0);
  }

  async function login(){
    const obj = {email,senha};
    const res = await axios.post(api + 'login.php' , obj);
     if(res.data.success == 'Dados Incorretos!')
     {
       mensagemDadosIncorretos();
     }
     else
     {
       setAbrirLogin(false);
     }
  }
   function buscarDados()
   {
      listarDados();  
   }


  return (
    <View>
        <Modal
      animationType="slide"
      transparent={false}
      visible={abrirLogin}
      >
 <SafeAreaView style={estilos.modal}>         

    <View style={estilos.modalHeader}>

       <Text style={estilos.textoModal}>Faça seu Login</Text>
    </View>

<Animatable.View  
animation="bounceInUp"
useNativeDriver  >

<TextInput 
 type="email"
 style={estilos.input}
 placeholder="Insira seu Email"
 value={email}
 onChangeText={ (email) => setEmail(email)}
/>
<TextInput 
 secureTextEntry={true}
 style={estilos.input}
 placeholder="Insira sua Senha"
 value={senha}
 onChangeText={ (senha) => setSenha(senha)}
/>

<TouchableOpacity  
      style={estilos.botaoModal}
      onPress={login}
      >
    <Text  style={estilos.textoBotaoModal}>Login</Text>
</TouchableOpacity>
    </Animatable.View>
   </SafeAreaView>
  </Modal> 

  </View>
  );
}

const estilos = StyleSheet.create({
  modal:{
    flex: 1,
    backgroundColor:'#b2b2b2'    
  },

  textoModal:{    
    color: '#FFF',    
    marginLeft: 15,
    fontSize:25,      
  },

  modalHeader:{    
   marginLeft:10,
   marginTop:20,
   alignItems:'center',
   flexDirection:'row',
   marginBottom:30,    
  },

  input:{
    backgroundColor: '#FFF',
    borderRadius: 5,
    margin: 8,
    padding: 8,
    color: '#000',
    fontSize:13
  },
  botaoModal:{
    backgroundColor: '#00335c',
    borderRadius: 5,
    margin: 5,
    padding: 12,
    color: '#FFF',
    alignItems:'center',
    justifyContent:'center',    
  },
  textoBotaoModal:{
    fontSize:16,
    color:'#FFF',
  },
  navbar:{
    backgroundColor: '#00335c',
    padding: 12,
    color: '#FFF',
    flexDirection:'row',
    marginTop: 35,
  },
  textonavbar:{
    fontSize:20,
    color:'#FFF',
    marginTop: 4,
    marginBottom: 2,
  },
  botao:{
    position: 'absolute', 
    right: 13,
    marginTop: 11,
  },
  grid:{
    marginTop: 8,    
  },
  griditem:{
    padding: 11,
    borderBottomColor: "#dbdbdb",
    borderBottomWidth: StyleSheet.hairlineWidth
  },

  gridbotaoEditar:{
    position: 'absolute',
    right:40,
    color:'#5c7ef6',
  },

  gridbotaoExcluir:{
    position: 'absolute',
    right:15,
    color:'#cc1414',
  },

  inputBuscar:{
    backgroundColor: '#FFF',
    borderRadius: 5,
    margin: 8,
    padding: 8,
    color: '#000',
    fontSize:15,
    borderBottomColor: "#767676",
    borderBottomWidth: StyleSheet.hairlineWidth,
    width:'100%',
    position:'relative',    
  },

  ViewinputBuscar:{
    flexDirection:'row',
  },

  iconeBuscar:{
   position:'absolute',
   right:20,
   top: 15,
  },
});
