import React, {useEffect,useState} from 'react';
import { StyleSheet, Text, View, TouchableOpacity, SafeAreaView,TextInput,Alert,ScrollView,Modal} from 'react-native';

import axios from 'axios';
import {Ionicons} from '@expo/vector-icons';


export default function App() {

  function mensagemDelete(id)
    {
      Alert.alert(
        "Exluir Registro",
        "Deseja exluir este registro? :(",
        [
           {text: "Não", 
           onPress:() => console.log("Cancel Pressed"),
           style: "cancel"
           },
           {text: "sim", 
           onPress:() => deleteItem(id)
           }
        ], 
        {cancelable: true}
      );
    }  

  const mensagemDuplicidade=() =>
  Alert.alert(
    "Erro ao salvar",
    "Email Já Cadastrado",
    [
      {text: "OK", onPress:() => setAbrir(true)}
    ], 
    {cancelable: true}
  );

  const mensagemDadosIncorretos = () =>

  Alert.alert(
         "Erro ao Logar",
         "Dados Incorretos",
         [
           {text: "OK", onPress: ()=> setAbrirLogin(true)}
         ],
           {cancelable: true}
  );

  const api= 'http://192.168.1.73/apireact/';
  const [lista,setLista] = useState([]);
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [id, setId] = useState('');
  const [buscar, setBuscar] = useState('');
  const [abrir, setAbrir] = useState(true);
  const [abrirLogin, setAbrirLogin] = useState(false);


  useEffect(() => {
    listarDados();
  },[])

async function listarDados(){
  const res= await axios.get(api + 'listar.php?busca=' + buscar);
  setLista(res.data.result);
}

async function add(){
  const obj={nome,email,senha,id};

  if(id > 0)
  {
    const res= await axios.post(api + 'editar.php',obj);
    if(res.data.success == true)
    {
      limparCampos();
    }
  }
else
{

  const res= await axios.post(api + 'add.php',obj);

  if(res.data.success == true)
  {
    limparCampos();
  }

  if(res.data.success == 'Email já Cadastrado!')
  {
    mensagemDuplicidade();
  }
}
  listarDados();
  setAbrir(false);
}

function limparCampos(){ 
  setNome('');
  setEmail('');
  setSenha('');
  setId('0');
} 

async function getItem(id)
{
  const res= await axios.get(api + 'buscarId.php?id=' + id);
  setId(res.data.id);
  setNome(res.data.nome);
  setEmail(res.data.email);
  setSenha(res.data.senha);
  setAbrir(true);
}

async function deleteItem(id)
{
  const res= await axios.get(api + 'excluir.php?id=' + id);
  limparCampos();
}

async function login(){
  const obj = {email,senha};
  const res = await axios.post(api + 'login.php' , obj);
   if(res.data.success == 'Dados Incorretos!')
   {
     mensagemDadosIncorretos();
   }
   else
   {
     setAbrirLogin(false);
   }
}
 function buscarDados()
 {
    listarDados();  
 }

  return (
    <View>
      <View style={estilos.navbar}>
        <Text style={estilos.textonavbar}>Lista de Usuários</Text>

        <TouchableOpacity
          style={estilos.botao}
          onPress={ () => setAbrir(true)}

        >
        <Ionicons name="ios-add" size={30} color="#FFF"></Ionicons>

        </TouchableOpacity>

      </View>

          <View style={estilos.ViewinputBuscar}>
              <TextInput
                style={estilos.inputBuscar}
                placeholder= "Buscar pelo nome"
                value={buscar}
                onChangeText= { (buscar) => setBuscar (buscar)}
                onChange = {buscarDados()}>
              </TextInput>
                  <Ionicons style={estilos.iconeBuscar}
                  name="ios-search" size={25}
                  color="#4b4a49"></Ionicons>  


          </View>
        <ScrollView>
          <View style={estilos.grid}>
            {lista.map(item => (
              <View 
                style={estilos.griditem}
                key={item.id}             
              >
                <Text style={{color:'#585858'}}>
                  {item.id} - {item.nome}
                </Text>

                <TouchableOpacity
                  style={estilos.gridbotaoEditar}
                  onPress={() => getItem(item.id)}

                >

                  <Ionicons name="ios-create" size={30} color="#50b9e1"></Ionicons>
                </TouchableOpacity>

                <TouchableOpacity
                  style={estilos.gridbotaoExcluir}
                  onPress={() => mensagemDelete(item.id)}

                >

                  <Ionicons name="ios-trash" size={30} color="#e15f50"></Ionicons>
                </TouchableOpacity>

              </View>

            ))}

          </View>
        </ScrollView>

        <Modal
          animationType='slide'
          visible={abrir}
        >
          <SafeAreaView
            style={estilos.modal}
          >
            <View
              style={estilos.modalHeader}
            > 
              <TouchableOpacity
                onPress={() => setAbrir(false)}
              >
               <Ionicons
                style={{marginLeft: 5, marginRight: 5}} 
                name="md-arrow-back"
                size={35}
                color= "#FFF"
               >
               </Ionicons> 
              </TouchableOpacity>

              <Text style={estilos.textoModal}>
                Inserir Usuário
              </Text>
            </View>

            <TextInput
              type="text"
              style={estilos.input}
              placeholder="Insira um nome"
              value={nome}
              onChangeText={(nome => setNome(nome))}
            >
            </TextInput>

            <TextInput
              type="text"
              style={estilos.input}
              placeholder="Insira seu e-mail"
              value={email}
              onChangeText={(email => setEmail(email))}
            >
            </TextInput>

            <TextInput
              type="text"
              style={estilos.input}
              placeholder="Insira sua senha"
              value={senha}
              onChangeText={(senha => setSenha(senha))}
              secureTextEntry={true}
            >
            </TextInput>

            <TouchableOpacity
              style={estilos.botaoModal}
              onPress={add}       
            >
              <Text style={estilos.textoBotaoModal}>Salvar</Text>
            </TouchableOpacity>

          </SafeAreaView>


        </Modal>

{/*tela login */ }      
<Modal
      animationType="slide"
      transparent={false}
      visible={abrirLogin}
      >
 <SafeAreaView style={estilos.modal}>         

    <View style={estilos.modalHeader}>

       <Text style={estilos.textoModal}>Faça seu Login</Text>
    </View>

<Animatable.View  
animation="bounceInUp"
useNativeDriver  >

<TextInput 
 type="email"
 style={estilos.input}
 placeholder="Insira seu Email"
 value={email}
 onChangeText={ (email) => setEmail(email)}
/>
<TextInput 
 secureTextEntry={true}
 style={estilos.input}
 placeholder="Insira sua Senha"
 value={senha}
 onChangeText={ (senha) => setSenha(senha)}
/>

<TouchableOpacity  
      style={estilos.botaoModal}
      onPress={login}
      >
    <Text  style={estilos.textoBotaoModal}>Login</Text>
</TouchableOpacity>
    </Animatable.View>
   </SafeAreaView>
  </Modal> 

</View>
  );
}

