import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Modal, TouchableOpacity, SafeAreaView, TextInput, Alert, ScrollView } from 'react-native';

import axios from 'axios'; 
import {Ionicons} from '@expo/vector-icons';
import * as Animatable from 'react-native-animatable';

export default function App() {

  const mensagemDadosIncorretos = () => 

  Alert.alert(
    "Erro ao Logar",
    "Dados Incorretos!",
    [
      {text: "OK", onPress: () => setAbrirLogin(true)}
    ],
    {cancelable: true}
  );


  function mensagemDelete(id)
  {
    Alert.alert(
      "Excluir Registro",
      "Deseja Excluir este Registro?",
      [
        {text: "Não", 
         onPress: () => console.log("Cancel Pressed"),
         styl:"cancel"
        },
        {
          text: "Sim",
          onPress: () => deleteItem(id)
        }
      ],
      {cancelable: true}
   );
  }


  const mensagemDuplicidade = () => Alert.alert(
     "Erro ao salvar",
     "E-mail já cadastrado",
     [
       {text: "OK", onPress: () => setAbrir(true)}
     ],
     {cancelable: true}
  );

  const api = 'http://192.168.1.73/apireact/';
  const [lista, setLista] = useState([]);
  const [modelo, setModelo] = useState('');
  const [ano, setAno] = useState('');
  const [placa, setPlaca] = useState('');
  const [id, setId] = useState('');
  const [buscar, setBuscar] = useState('');


  const [abrir, setAbrir] = useState(false);
  const [abrirLogin,setAbrirLogin] = useState(true);

  useEffect(() => {
    listarDados();
  }, [] )

  async function listarDados(){
    const res= await axios.get(api + 'listar.php?busca=' + buscar);
    setLista(res.data.result);
  }

  async function add(){
    const obj= {modelo,ano,placa,id};

    if(id >0)
    {
      const res= await axios.post(api + 'editar.php',obj);
    }
    else{
    const res= await axios.post(api + 'add.php',obj);

    if(res.data.sucess == true)
    {
      limparCampos();
    }
    if(res.data.sucess == 'Email já cadastrado!')
    {
      mensagemDuplicidade();
    }
  } 
    listarDados();
    setAbrir(false);
  }

  async function getItem(id)
  {
    const res= await axios.get(api + 'buscarId.php?id=' + id)
    setId(res.data.id);
    setModelo(res.data.modelo);
    setAno(res.data.ano);
    setPlaca(res.data.placa);
    setAbrir(true);
  }

  async function deleteItem(id) 
  {
    const res= await axios.get(api + 'excluir.php?id=' + id);
    listarDados();
  } 

  function limparCampos(){
    setModelo('');
    setAno ('');
    setPlaca('');
    setId(0);
  }

  async function login(){
    const obj= {modelo,placa};
    const res= await axios.post(api + 'login.php', obj);
    if(res.data.success == 'Dados Incorretos!'){
      mensagemDadosIncorretos();
    }
    else{
      setAbrirLogin(false);
    }
  }

  function buscarDados()
  {
    listarDados();
  }

  return (
    <View>
      <View style={estilos.navbar}>
        <Text style={estilos.textonavbar}> Lista de Carros </Text>

      <TouchableOpacity 
        style={estilos.botao}
        onPress={() => setAbrir(true)}>

        <Ionicons name="ios-add" size={30} color="#FFF"> </Ionicons>

      </TouchableOpacity>

      </View>

        <View style={estilos.ViewinputBuscar}>
          <TextInput
            style={estilos.inputBuscar}
            placeholder="Buscar pela placa"
            value={buscar}
            onChangeText={(buscar) => setBuscar(buscar)}
            onChange= {buscarDados()}

          >
          </TextInput>
            <Ionicons style={estilos.iconeBuscar} name="ios-search" siz={25} color="#4b4a49"></Ionicons>

        </View>

      <ScrollView> 
        <View styles={estilos.grid}> 
          {lista.map(item => (
            <View style={estilos.griditem} key={item.id}>
              <Text style={{color:'#585858'}}>
                {item.id} - {item.modelo} - {item.ano} - {item.placa} 
              </Text>  

              <TouchableOpacity style={estilos.gridbotaoEditar} onPress={() => getItem(item.id)}>
                <Ionicons name="ios-create" size={30} color="#50b9e1"></Ionicons>
              </TouchableOpacity>

              <TouchableOpacity style={estilos.gridbotaoExcluir} onPress={() => mensagemDelete(item.id)}>
                <Ionicons name="ios-trash" size={30} color="#e15f50"></Ionicons>
              </TouchableOpacity>

            </View>
          ))}
        </View>
      </ScrollView>

        <Modal
          animationType='slide'
          visible={abrir}
        > 
        <SafeAreaView style={estilos.modal}>
          <View style={estilos.modalHeader}>
            <TouchableOpacity
              onPress={() => setAbrir(false)} 
            > 
            <Ionicons style={{marginLeft:5, marginRight:5}} name="md-arrow-back" size={35} color="#FFF"> </Ionicons>
            </TouchableOpacity>
            <Text style={estilos.textoModal}> Inserir Carro </Text>
          </View>

            <TextInput
              type="text"
              style={estilos.input}
              placeholder="Insira o modelo do carro"
              value={modelo}
              onChangeText={(modelo) => setModelo(modelo)}>
            </TextInput>

            <TextInput
              type="text"
              style={estilos.input}
              placeholder="Insira o ano do carro"
              value={ano}
              onChangeText={(ano) => setAno(ano)}>
            </TextInput>

            <TextInput
              secureTextEntry={false}
              style={estilos.input}
              placeholder="Insira a placa do carro"
              value={placa}
              onChangeText={(placa) => setPlaca(placa)}>
            </TextInput>

            <TouchableOpacity style={estilos.botaoModal} onPress={add}>
              <Text style={estilos.textoBotaoModal}> Salvar </Text> 
            </TouchableOpacity>

        </SafeAreaView>
        </Modal>

        <Modal
          animationType="slide"
          transparent={false}
          visible={abrirLogin}
        >
      <SafeAreaView style={estilos.modal}>         

        <View style={estilos.modalHeader}>
          <Text style={estilos.textoModal}>Faça seu Login</Text>
        </View>

        <Animatable.View  
          animation="bounceInUp"
          useNativeDriver  >

          <TextInput 
            type="email"
            style={estilos.input}
            placeholder="Insira o modelo do carro"
            value={modelo}
            onChangeText={ (modelo) => setModelo(modelo)}
          />

          <TextInput 
            type="email"
            style={estilos.input}
            placeholder="Insira a placa do carro"
            value={placa}
            onChangeText={ (placa) => setPlaca(placa)}
          />

          <TouchableOpacity style={estilos.botaoModal} onPress={login}>
            <Text  style={estilos.textoBotaoModal}>Login</Text>
          </TouchableOpacity>
        </Animatable.View>
      </SafeAreaView>
  </Modal> 

    </View>
  );
}

const estilos = StyleSheet.create({
  modal:{
    flex: 1,
    backgroundColor:'#b2b2b2'    
  },

  textoModal:{    
    color: '#FFF',    
    marginLeft: 15,
    fontSize:25,      
  },

  modalHeader:{    
   marginLeft:10,
   marginTop:20,
   alignItems:'center',
   flexDirection:'row',
   marginBottom:30,    
  },

  input:{
    backgroundColor: '#FFF',
