import React, { Component } from 'react';
import { StyleSheet, Text, View, TouchableOpacity} from 'react-native';


export default class App extends Component {


  render(){
    return(
      < View style={styles.container}>
        <Text style={styles.bancoLogo}>ALERTA DE ALAGAMENTO</Text>
          <View>
          <Text style={styles.aviso01}>NORMAL</Text>
          <Text style={styles.aviso02}>MÉDIO</Text>
          <Text style={styles.aviso03}>ACIMA DA MÉDIA</Text>

          <TouchableOpacity style={styles.emerge}>
              <Text style={styles.botaoTexto}>Chamda de emergência</Text>
              </TouchableOpacity>
            </View>
          </View>

      );
    }
  }
  const styles = StyleSheet.create({
    container:{
      flex: 1,
      paddingTop: 20,

    },
    textoNome:{
    fontSize: 17,
    color: '#000000',
    fontWeight:'bold'
    },
    botao:{
      height: 35,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: '#000000',
      borderRadius: 150,
      margin: 20
    },
     botaoTexto:{
       fontSize: 20,
       fontWeight: 'bold',
       color: '#FFFFFF'
     },
     bancoLogo:{
      textAlign: 'center',
      fontSize: 65,
      fontWeight: 'bold',
      position: 'absolute', 
      right: '1%',
      marginTop: 200,
    },
    aviso01:{
      textAlign: 'center',
      fontSize: 30,
      fontWeight: 'bold',
      position: 'absolute', 
      right: 300,
      marginTop: 650,
    },
    aviso02:{
      textAlign: 'center',
      fontSize: 30,
      fontWeight: 'bold',
      position: 'absolute', 
      right: 330,
      marginTop: 750,
    },
    aviso03:{
      textAlign: 'center',
      fontSize: 30,
      fontWeight: 'bold',
      position: 'absolute', 
      right: 200,
      marginTop: 850,
    },

    emerge:{
      height: 60,
      width:  300,
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'red',
      borderRadius: 10,
      margin: 20,
      position: 'absolute', 
      right:100,
      marginTop: 1100,
     },
  });
